INSERT INTO `nb_pages` (`id`, `menu`, `title`, `htitle`, `dscr`, `text`, `code`, `lang`, `new_prod`) VALUES
(1, 'Главная', 'Строительная компания, Киев - Профессиональное строительство и ремонт в Киеве по доступным ценам', 'Строительная компания в Киеве', 'Наша строительная компания в Киеве предлагает надежное строительство и ремонт объектов промышленного и гражданского назначения в Киеве и Киевской области. И всё это по доступным ценам.', '<p>Наша компания является профессиональной многопрофильной строительной организацией, которая предлагает надежное строительство и ремонт объектов промышленного и гражданского назначения в Киеве и Киевской области. При необходимости мы выполняем функции генерального подрядчика, быстро и качественно организуя ремонтные и строительные работы высокой сложности и больших объемов.</p><p>Специализация нашей строительной компании – выполнение строительства и ремонта «под ключ» по доступным ценам. Сюда входит полный комплекс необходимых ремонтных и строительных услуг от разработки дизайн-проекта до внутренней финишной отделки. Это позволяет нашим клиентам обеспечить максимально удобные и эффективные условия возведения жилых и нежилых зданий и сооружений, их реконструкции, а также ремонта коммерческих, производственных, административных, общественных помещений.</p>', 'index', 'ru', 1),
(2, 'Main page', 'Main page', 'Main page', 'Description', '<p>Text</p>', 'index', 'en', 1),
(5, 'Услуги', 'Строительные услуги, Киев - Строительство, ремонт, отделка зданий и помещений', 'Ремонтные и строительные услуги', 'Мы предоставляем строительные услуги в Киеве и Киевской области по всем направлениям проектирования, строительства, ремонта и отделки зданий, сооружений и помещений. При этом вы можете заказать ремонтные и строительные работы как отдельно, так и в комплексе «под ключ».', '<p><span style=\"color:#ff6600\">Мы предоставляем строительные услуги в Киеве и Киевской области по всем направлениям проектирования, строительства, ремонта и отделки зданий, сооружений и помещений. При этом вы можете заказать ремонтные и строительные работы как отдельно, так и в комплексе &laquo;под ключ&raquo;.</span></p>\r\n\r\n<p>При необходимости мы выполняем функцию генподрядчика, планируя, организуя и контролируя все этапы реализации объекта от разработки проекта до сдачи в эксплуатацию.</p>\r\n\r\n<p>Большой профессиональный опыт, ориентация на интересы заказчика, а также сотрудничество с лучшими специалистами различных отраслей строительства и ремонта позволяет вам недорого заказать качественные ремонтные и строительные услуги нашей строительной компании:</p>\r\n\r\n<ol>\r\n	<li>Разработка проекта зданий и сооружений, дизайна интерьера и экстерьера. Составление сметы строительно-ремонтных работ.</li>\r\n	<li>Планирование и устройство сети внутреннего и внешнего инженерно-технического обеспечения (монтаж и демонтаж систем и коммуникаций тепло-, газо-, электро-, водоснабжения, водоотведения и вентиляции, связи).</li>\r\n	<li>Строительство зданий и сооружений жилого и нежилого назначения из любых материалов и различным технологиям, в т.ч. услуги строительства &laquo;под ключ&raquo;.</li>\r\n	<li>Монтаж внутренних и наружных конструкций из металла, дерева, поликарбоната, профнастила и прочего материала.</li>\r\n	<li>Косметический и капительный ремонт зданий и помещений, в т.ч. со сменой их функционального назначения, реконструкцией и перепланировкой.</li>\r\n	<li>Внутренние и внешние отделочные работы, услуги по тепло-, гидро- и звукоизоляции зданий и помещений.</li>\r\n	<li>Благоустройство территории (услуги ландшафтного дизайнера, монтаж заборов, беседок, навесов из поликарбоната и т.п.).</li>\r\n</ol>\r\n\r\n<p>Оказание строительных услуг осуществляется на договорной основе и подразумевает четкое соблюдение указанных сроков и объема работ, а также предоставляет гарантию на выполненное нами строительство и ремонт.</p>\r\n\r\n<p><span style=\"color:#ff6600\">Чтобы получить подробную информацию и узнать цены на строительно-ремонтные услуги, обратитесь к нашим менеджерам по телефону или с помощью e-mail.</span></p>\r\n', 'services', 'ru', 3),
